import React,{useEffect, useState} from 'react';
import TodoFilter from './TodoFilter';
import TodoFooter from './TodoFooter';
import TodoForm from './TodoForm';
import TodoHeader from './TodoHeader';
import TodoList from './TodoList';

const todos = [
	{
		id: 1,
		title: 'Uống một tách trà nóng',
		isCompleted: false
	},
	{
		id: 2,
		title: 'Đến Academy học lập trình',
		isCompleted: false
	},
	{
		id: 3,
		title: 'Ăn trưa cùng crush',
		isCompleted: false
	},
	{
		id: 4,
		title: 'Thực hành lập trình',
		isCompleted: true
	}
];

function TodoApp() {
	const [todoItems, setToItems] = useState([]);
	const [filterItems, setFilterItems] = useState([]);
	const [state, setState] = useState(0);

	useEffect(()=> {
		setToItems(todos)
		setFilterItems(todoItems)
	},[]);

	useEffect(()=> {
		// console.log(state);
		if(state === 0) {
			setFilterItems(todoItems.filter(i=> i.isCompleted))
		}
		if(state === 1) {
			setFilterItems(todoItems.filter(i=> !i.isCompleted))
		}
		if(state === 2) {
			setFilterItems(todoItems.filter(i=> i.isCompleted))
		}
		else {
			setFilterItems(todoItems)
		}
	},[state, todoItems])

	function handleAddItem(item) {
		let i = {
			id: Math.random()*100000,
			title: item,
			isCompleted: false
		}
		setToItems([...todoItems,i])
	}
	function newState(value) {
		setState(value)
	}
	return (
			<>
				<div className="app-container">
					<TodoHeader />

					<div className="todo-container">
						<TodoForm handleAddItem={handleAddItem}/>
						<TodoFilter len={filterItems.length} changeState= {newState}/>
						<TodoList todos={filterItems}/>
					</div>
				</div>

				<TodoFooter />
			</>
		);
	}


export default TodoApp;
