import React,{useEffect, useState} from 'react';
import TodoFilter from './TodoFilter';
import TodoFooter from './TodoFooter';
import TodoForm from './TodoForm';
import TodoHeader from './TodoHeader';
import TodoList from './TodoList';

const todos = [
	{
		id: 1,
		title: 'Uống một tách trà nóng',
		isCompleted: false
	},
	{
		id: 2,
		title: 'Đến Academy học lập trình',
		isCompleted: false
	},
	{
		id: 3,
		title: 'Ăn trưa cùng crush',
		isCompleted: false
	},
	{
		id: 4,
		title: 'Thực hành lập trình',
		isCompleted: false
	}
];

function TodoApp() {
	const [todoItems, setToItems] = useState([]);

	useEffect(()=> {
		setToItems(todos)
	},[])
	function handleAddItem(item) {
		let i = {
			id: Math.random()*100000,
			title: item,
			isCompleted: false
		}
		setToItems([...todoItems,i])
		console.log([]);
	}
		return (
			<>
				<div className="app-container">
					<TodoHeader />

					<div className="todo-container">
						<TodoForm handleAddItem={handleAddItem}/>
						<TodoFilter len={todoItems.length}/>
						<TodoList todos={todoItems}/>
					</div>
				</div>

				<TodoFooter />
			</>
		);
	}


export default TodoApp;
